import React from 'react';
import { Box, Heading, Text, Stack, Button } from '@chakra-ui/react';
import { motion } from "framer-motion";

const greeting = "Hello, I am Tortrong Yang!"
const bio1 = "Frontend Developer | React & JavaScript"
const bio2 = "Computer Science graduate building responsive web applications with React, JavaScript, and modern UI libraries."

const MotionBox = motion(Box);

const LandingSection = () => {
  return (
    <MotionBox
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1 }}
      textAlign="center"
      py={20}
      px={4}
    >
      <Stack spacing={4}>
        <Heading>{greeting}</Heading>
        <Text fontSize="xl">{bio1}</Text>
        <Text fontSize="md">{bio2}</Text>
        <Button colorScheme="blue" size="lg">View Projects</Button>
      </Stack>
    </MotionBox>
  );
};

export default LandingSection;
