import React from 'react';
import { Box, Heading, Text, VStack, Button } from '@chakra-ui/react';
import { motion } from "framer-motion";

const greeting = "Hello, I am Tortrong Yang!"
const bio1 = "Frontend Developer | React & JavaScript"
const bio2 = "Computer Science graduate building responsive web applications with React, JavaScript, and modern UI libraries."

const MotionBox = motion(Box);

const LandingSection = () => {
  return (
    <MotionBox
      minH="100vh"
      display="flex"
      alignItems="center"
      justifyContent="center"
      px={6}
      initial={{ opacity: 0, y: 40 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.8, ease: "easeOut" }}
    >
       <Box textAlign="center" maxW="700px">
        {/* Name */}
        <Heading
          as="h1"
          size="2xl"
          mb={4}
          fontWeight="bold"
        >
          Tortrong Yang
        </Heading>
         {/* Role */}
        <Heading
          as="h2"
          size="md"
          color="gray.600"
          mb={6}
          fontWeight="medium"
        >
          Frontend Developer Â· React & JavaScript
        </Heading>

        {/* Short pitch */}
        <Text fontSize="lg" color="gray.700" mb={8}>
          Computer Science graduate building responsive, user-focused web
          applications using React, modern JavaScript, and UI frameworks.
        </Text>

        {/* Call-to-action buttons */}
        <Stack
          direction={{ base: "column", sm: "row" }}
          spacing={4}
          justify="center"
        >
          <Button
            colorScheme="blue"
            size="lg"
            as="a"
            href="#projects"
          >
            View Projects
          </Button>

          <Button
            variant="outline"
            size="lg"
            as="a"
            href="#contact"
          >
            Contact Me
          </Button>
        </Stack>
      </Box>
    </MotionBox>
  );
};

export default LandingSection;
