import React from "react";
import { Avatar, Heading, VStack } from "@chakra-ui/react";
import FullScreenSection from "./FullScreenSection";
import Header from "./Header";

const greeting = "Hello, I am Tortrong!";
const bio1 = "An aspiring frontend developer";
const bio2 = "specialised in React, JavaScript, HTML, and CSS";

// Implement the UI for the LandingSection component according to the instructions.
// Use a combination of Avatar, Heading and VStack components.
const LandingSection = () => (
  <FullScreenSection
    justifyContent="center"
    alignItems="center"
    isDarkBackground
    backgroundColor="#2A4365"
  >
    <VStack margin="150px auto">
      <Avatar src="https://i.pravatar.cc/150?img=7"></Avatar>
      <p>{greeting}</p>
      <Heading>
          {bio1}
      </Heading>
      <Heading>
          {bio2}
      </Heading>
    </VStack>
  </FullScreenSection>
);

export default LandingSection;
