import { Heading, HStack, Image, Text, VStack } from "@chakra-ui/react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";
import React from "react";
import ProjectsSection from "./ProjectsSection";

const Card = ({ title, description, imageSrc }) => {
  // Implement the UI for the Card component according to the instructions.
  return (
    <HStack bg={"white"} borderRadius="10px" align="stretch">
      <VStack>
          <Image borderRadius="10px" src={imageSrc} objectFit="cover"></Image>
          <Heading as="h1" fontSize="13px" color="black">{title}</Heading>
          <Text color="gray" padding="5px" fontSize="11px" noOfLines={3}>{description}</Text>
             <Text color="black" fontSize="sm" cursor="pointer" fontWeight="medium">
          See more <FontAwesomeIcon icon={faArrowRight} cursor="pointer" size="1x" color="black"/>
            </Text>
      </VStack>
    </HStack>
  );
  // You should be able to implement the component with the elements imported above.
  // Feel free to import other UI components from Chakra UI if you wish to.
};

export default Card;
