import React from "react";
import FullScreenSection from "./FullScreenSection";
import { Box, Heading } from "@chakra-ui/react";
import Card from "./Card"; 

//Array of Project Details
const projects = [
  {
    title: "Little Lemon Restauraunt Website",
    description:
      "A website for the Little Lemon restaurant, showcasing their menu, location, and contact information.",
    getImageSrc: () => require("../images/littlelemon.png"),
    url: "https://tyang019.github.io/little_lemon_project/"
  },
  {
    title: "React Infinite Scroll",
    description:
      "A scrollable bottom sheet with virtualisation support, native animations at 60 FPS and fully implemented in JS land ðŸ”¥ï¸",
    getImageSrc: () => require("../images/photo2.jpg"),
  },
  {
    title: "Photo Gallery",
    description:
      "A One-stop shop for photographers to share and monetize their photos, allowing them to have a second source of income",
    getImageSrc: () => require("../images/photo3.jpg"),
  },
  {
    title: "Event planner",
    description:
      "A mobile application for leisure seekers to discover unique events and activities in their city with a few taps",
    getImageSrc: () => require("../images/photo4.jpg"),
  },
];

const ProjectSection = () => (
  <FullScreenSection
    isDarkBackground={false}  
    backgroundColor="#F0F1F3"
    alignItems="flex-start"
    padding="2rem"
  >
    <Heading color="#" as="h2" size="xl" mb="4">
      Projects
    </Heading>
    <Box display="grid" gridTemplateColumns="repeat(auto-fill, minmax(300px, 1fr))" gridGap="2rem">
      {projects.map((project, index) => (
        <Card
          key={index}
          title={project.title}
          description={project.description}
          imageSrc={project.getImageSrc()}
        />
      ))}
    </Box>
  </FullScreenSection>
);

export default ProjectSection;
